import React, { useEffect, useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";

const initialState = {
  name: "",
  password: "",
};

export default function UserLogin() {
  const [user, setUser] = useState(initialState);
  let navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch("http://localhost:8080/login/user", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(user),
    }).then((response) => {
      if (response.ok) {
        sessionStorage.setItem("name", user.name);
        sessionStorage.setItem("role", "user");
        navigate("/movies");
      }else{
        alert("Invalid credentials.");
      }
    });
  };

  return (
    <div className="container px-4 px-lg-5">
      <div className="row gx-4 gx-lg-5 align-items-center my-5">
        <h3 id="titLogin" style={{ color: "#492000" }}>Log in to purchase tickets</h3>
        <p></p>
        <div className="col-md-6">
          <form>
            <div className="mb-3">
              <label className="form-label" style={{ color: "#492000" }}>Username:</label>{" "}
              <input
                id="boxUsername"
                type="text"
                className="form-control"
                name="name"
                value={user.name}
                onChange={(e) =>
                  setUser({ ...user, [e.target.name]: e.target.value })
                }
              />
            </div>
            <div className="mb-3">
              <label className="form-label" style={{ color: "#492000" }}>Password:</label>{" "}
              <input
                id="boxPassword"
                type="password"
                className="form-control"
                name="password"
                value={user.password}
                onChange={(e) =>
                  setUser({ ...user, [e.target.name]: e.target.value })
                }
              />
            </div>
            <button
              id="btnLogin"
              type="submit"
              className="btn"
              style={{ backgroundColor: "#006400", color: "white" }}
              onClick={handleSubmit}
            >
              Log In
            </button>
          </form>
          <div class="cta-inner bg-faded text-center rounded">
            <p></p>
            <NavLink
              id="linkRegister"
              className="nav-link"
              style={{ textDecoration: "underline", color: "FFA500" }}
              to={"/register"}
            >
            <h3>Are you new here? Register!</h3>
            </NavLink>
            <br></br>
            <NavLink
              className="nav-link"
              style={{ textDecoration: "underline", color: "#C20605" }}
              to={"/login-admin" }
            >
              Log in as Admin instead
            </NavLink>
          </div>
        </div>
      </div>
    </div>
  );
}
